import React, { useState, useEffect } from "react";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider
} from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";
import "moment/locale/ru";
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import "../dtpic/dtpic.css"

import axios from "axios";
import { useHistory, Link } from "react-router-dom";
import "../datatab/datatab.css"



const click = () => {
  console.log('http://localhost:62499/api/zplist/'+document.getElementById('start').value + '&' + document.getElementById('end').value)
  this.loadUsers()
}

function Dtpic(props) {
  const [selectedDate, handleDateChange] = useState({
    start: null,
    end: null
  });

  const handleChange = name => date => {
    //console.log("handleChange", date);

    handleDateChange({ ...selectedDate, [name]: date });
  };

  //console.log("value", selectedDate.start);

  const history = useHistory();
  const [users, setUser] = useState([]);

  /*useEffect(() => {
    loadUsers();
  }, []);
*/
  

  const loadUsers = async () => {
    const result = await axios.get(
      `http://localhost:62499/api/zplist/2021-08-02&2021-08-04`
    );
    setUser(result.data);
  };

  return (
    <div>
      <div className="botmarg">
      <Grid container spacing={3}>
        <Grid item xs>
          <MuiPickersUtilsProvider locale="ru" utils={MomentUtils}>
            <KeyboardDatePicker
              id="start"
              name="start"
              label="From"
              format="YYYY-MM-DD"
              autoOk
              value={selectedDate.start}
              variant="inline"
              onChange={handleChange("start")}

            />
            <KeyboardDatePicker
              id="end"
              name="end"
              label="To"
              format="YYYY-MM-DD"
              autoOk
              value={selectedDate.end}
              variant="inline"
              onChange={handleChange("end")}
            />
          </MuiPickersUtilsProvider>
        </Grid>
        <Grid  item xs>
          <Button  onClick={click} variant="contained" color="secondary" >
            Получить
          </Button>
        </Grid>
        <Grid item xs>

        </Grid>
      </Grid>

      


    </div>
    <div className="home-page">
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th className="th1" scope="col">#</th>
            <th className="th1" scope="col">Фамилия</th>
            <th className="th1" scope="col">Кол-во SKU</th>
            <th className="th1" scope="col">Кол-во SKU проверено</th>
            
            <th className="th1"scope="col">кол-во ошибок GR</th>
            <th className="th1"scope="col">кол-во ошибок SA</th>
            <th className="th1"scope="col">кол-во ошибок по BMC</th>
            <th className="th1" scope="col">Сумма штрафа</th>
            <th className="th1">Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr>
              <th className="th1" scope="row">{index + 1}</th>
              <td className="td1" >{user.id}</td>
              <td className="td1">{user.UserOperationID_21_Col}</td>
              <td className="td1">{user.UserOperationID_22_Col}</td>

              <td className="td1"><input type="text" id="GR" name="GR"/></td>
              <td className="td1"><input type="text" id="SA" name="SA"/></td>
              <td className="td1"><input type="text" id="WMS" name="WMS"/></td>
              <td className="td1"><input type="text" id="Forfeit" name="Forfeit"/></td>
              
              <Button
                variant="contained"
                color="primary"
                onClick={() => history.push(`/edituser/${user.id}`)}
              >
                Edit
              </Button>
              
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  
    </div>
    );
}

export default Dtpic;
