import React, { useState, useEffect } from "react";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider
} from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";
import "moment/locale/ru";
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import "../dtpic/dtpic.css"

import axios from "axios";
import { useHistory, Link } from "react-router-dom";
import "../datatab/datatab.css"

import TextField from '@material-ui/core/TextField';


function Dtpic(props) {
  const [selectedDate, handleDateChange] = useState({
    start: null,
    end: null
  });

  const handleChange = name => date => {


    handleDateChange({ ...selectedDate, [name]: date });
  };

 

  const history = useHistory();
  const [users, setUser] = useState([]);
 

  const click = async () => {
    const result = await axios.get(
      'http://localhost:62499/api/zplist/'+document.getElementById('start').value + '&' + document.getElementById('end').value
    );
    setUser(result.data);
  };

  return (
    <div>
      <div className="botmarg">
      <Grid container spacing={3}>
        <Grid item xs>
          <MuiPickersUtilsProvider locale="ru" utils={MomentUtils}>
            <KeyboardDatePicker
              id="start"
              name="start"
              label="From"
              format="YYYY-MM-DD"
              autoOk
              value={selectedDate.start}
              variant="inline"
              onChange={handleChange("start")}

            />
            <KeyboardDatePicker
              id="end"
              name="end"
              label="To"
              format="YYYY-MM-DD"
              autoOk
              value={selectedDate.end}
              variant="inline"
              onChange={handleChange("end")}
            />
          </MuiPickersUtilsProvider>
        </Grid>
        <Grid  item xs>
          <Button  onClick={click} variant="contained" color="secondary" >
            Получить
          </Button>
        </Grid>
        <Grid item xs>

        </Grid>
      </Grid>

      


    </div>
    <div >
      <table className="mytab">
        <thead className="thead-dark">
          <tr>
            <th className="th1" scope="col">#</th>
            <th className="th1" scope="col">Фамилия</th>
            <th className="th1" scope="col">Кол-во SKU</th>
            <th className="th1" scope="col">Кол-во SKU проверено</th>
            
            <th className="th1"scope="col">кол-во ошибок GR</th>
            <th className="th1"scope="col">кол-во ошибок SA</th>
            <th className="th1"scope="col">кол-во ошибок по BMC</th>
            <th className="th1" scope="col">Сумма штрафа</th>
            <th className="th1" scope="col">даты</th>
            <th className="th1">Action</th>
          </tr>
        </thead>
        <tbody className="mytab">
          {users.map((user, index) => (
            <tr key={user.id}>
              <th className="th1" scope="row">{index + 1}</th>
              <td className="td1">{user.id}</td>
              <td className="td1">{user.UserOperationID_21_Col}</td>
              <td className="td1">{user.UserOperationID_22_Col}</td>

              <td className="td1"><TextField  defaultValue={user.ErrGr_Col} variant="outlined"/></td>
              <td className="td1"><TextField  defaultValue={user.ErrSa_Col} variant="outlined"/></td>
              <td className="td1"><TextField  defaultValue={user.ErrWMS_Col} variant="outlined"/></td>
              <td className="td1"><TextField  defaultValue={user.forfeit} variant="outlined"/></td>


              <td className="td1">{user.OperationDate_Col}</td>
              
              <Button
                variant="contained"
                color="primary"
                onClick={() => history.push(`/edituser/${user.id}`)}
              >
                Edit
              </Button>
              
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  
    </div>
    );
}

export default Dtpic;
