import React, { useState } from "react";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider
} from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";
import "moment/locale/es";
import { alpha } from '@material-ui/core/styles'

const click =()=>{
    console.log(document.getElementById('start').value + document.getElementById('end').value)
  }

function Dtpic(props) {
  const [selectedDate, handleDateChange] = useState({
    start: null,
    end: null
  });

  const handleChange = name => date => {
    //console.log("handleChange", date);
    
    handleDateChange({ ...selectedDate, [name]: date });
  };

  //console.log("value", selectedDate.start);
  

  return (
    <div>
        <MuiPickersUtilsProvider  locale="es" utils={MomentUtils}>
      <KeyboardDatePicker
        id="start"
        name="start"
        label="From"
        format="YYYY-MM-DD"
        autoOk
        value={selectedDate.start}
        variant="inline"
        onChange={handleChange("start")}
        
      />
      <KeyboardDatePicker
        id="end"
        name="end"
        label="To"
        format="YYYY-MM-DD"
        autoOk
        value={selectedDate.end}
        variant="inline"
        onChange={handleChange("end")}
      />
    </MuiPickersUtilsProvider>
    <button onClick={click}>Получить</button>
    </div>
  );
}

export default Dtpic;
